(1031, '_transient_woocommerce_addons_data', 'O:8:"stdClass":10:{s:8:"featured";a:4:{i:0;O:8:"stdClass":5:{s:5:"title";s:20:"WooCommerce Bookings";s:5:"image";s:0:"";s:7:"excerpt";s:108:"Sell your time or date based bookings! Perfect for those wanting to offer appointments, services or rentals.";s:4:"link";s:114:"http://www.woothemes.com/products/woocommerce-bookings/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;249.00";}i:1;O:8:"stdClass":5:{s:5:"title";s:15:"Product Add-Ons";s:5:"image";s:0:"";s:7:"excerpt";s:104:"Allow your customers to customize products by adding input boxes, dropdowns, file uploads or checkboxes.";s:4:"link";s:109:"http://www.woothemes.com/products/product-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:2;O:8:"stdClass":5:{s:5:"title";s:25:"WooCommerce Subscriptions";s:5:"image";s:64:"https://www.woothemes.com/wp-content/uploads/2012/09/woosubs.png";s:7:"excerpt";s:85:"WC Subscriptions makes it easy to create and manage products with recurring payments.";s:4:"link";s:119:"http://www.woothemes.com/products/woocommerce-subscriptions/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:3;O:8:"stdClass":5:{s:5:"title";s:19:"Table Rate Shipping";s:5:"image";s:0:"";s:7:"excerpt";s:123:"Advanced, flexible shipping. Define multiple shipping rates based on location, price, weight, shipping class or item count.";s:4:"link";s:113:"http://www.woothemes.com/products/table-rate-shipping/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}}s:7:"popular";a:25:{i:0;O:8:"stdClass":5:{s:5:"title";s:19:"WooCommerce iOS App";s:5:"image";s:0:"";s:7:"excerpt";s:217:"Keep your finger on the pulse of your online shop with WooCommerce iOS. All of your shop''s catalog & performance reports are now just a tap away - quickly accessible & beautifully presented right there on your iPhone.";s:4:"link";s:100:"http://www.woothemes.com/woocommerce-ios/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:9:"&#36;4.99";}i:1;O:8:"stdClass":5:{s:5:"title";s:15:"Pay with Amazon";s:5:"image";s:116:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2013/08/AP_HLogo_215x35-3012530377._V360408447_.png";s:7:"excerpt";s:153:"Pay with Amazon is embedded directly into your WooCommerce store. Buyer interactions take place in embedded widgets, so the buyer never leaves your site.";s:4:"link";s:109:"http://www.woothemes.com/products/pay-with-amazon/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:9:"&#36;0.00";}i:2;O:8:"stdClass":5:{s:5:"title";s:15:"Product Add-Ons";s:5:"image";s:0:"";s:7:"excerpt";s:104:"Allow your customers to customize products by adding input boxes, dropdowns, file uploads or checkboxes.";s:4:"link";s:109:"http://www.woothemes.com/products/product-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:3;O:8:"stdClass":5:{s:5:"title";s:25:"WooCommerce Subscriptions";s:5:"image";s:64:"https://www.woothemes.com/wp-content/uploads/2012/09/woosubs.png";s:7:"excerpt";s:85:"WC Subscriptions makes it easy to create and manage products with recurring payments.";s:4:"link";s:119:"http://www.woothemes.com/products/woocommerce-subscriptions/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:4;O:8:"stdClass":5:{s:5:"title";s:6:"Stripe";s:5:"image";s:63:"https://www.woothemes.com/wp-content/uploads/2012/09/stripe.png";s:7:"excerpt";s:30:"Stripe gateway for WooCommerce";s:4:"link";s:100:"http://www.woothemes.com/products/stripe/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:5;O:8:"stdClass":5:{s:5:"title";s:20:"USPS Shipping Method";s:5:"image";s:61:"https://www.woothemes.com/wp-content/uploads/2012/09/usps.jpg";s:7:"excerpt";s:91:"Get shipping rates from the USPS API which handles both domestic and international parcels.";s:4:"link";s:114:"http://www.woothemes.com/products/usps-shipping-method/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:6;O:8:"stdClass":5:{s:5:"title";s:19:"UPS Shipping Method";s:5:"image";s:85:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2013/01/ups_logo.gif";s:7:"excerpt";s:90:"Get shipping rates from the UPS API which handles both domestic and international parcels.";s:4:"link";s:113:"http://www.woothemes.com/products/ups-shipping-method/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:7;O:8:"stdClass":5:{s:5:"title";s:17:"Authorize.Net AIM";s:5:"image";s:82:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2011/09/auth1.png";s:7:"excerpt";s:112:"Take credit card payments direct on your checkout using the Authorize.net (AIM) payment gateway for WooCommerce.";s:4:"link";s:111:"http://www.woothemes.com/products/authorize-net-aim/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:8;O:8:"stdClass":5:{s:5:"title";s:25:"Order/Customer CSV Export";s:5:"image";s:0:"";s:7:"excerpt";s:69:"Export orders and customers from WooCommerce to a CSV file with ease.";s:4:"link";s:118:"http://www.woothemes.com/products/ordercustomer-csv-export/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:9;O:8:"stdClass":5:{s:5:"title";s:19:"Table Rate Shipping";s:5:"image";s:0:"";s:7:"excerpt";s:123:"Advanced, flexible shipping. Define multiple shipping rates based on location, price, weight, shipping class or item count.";s:4:"link";s:113:"http://www.woothemes.com/products/table-rate-shipping/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:10;O:8:"stdClass":5:{s:5:"title";s:15:"Dynamic Pricing";s:5:"image";s:0:"";s:7:"excerpt";s:48:"Bulk discounts, role-based pricing and much more";s:4:"link";s:109:"http://www.woothemes.com/products/dynamic-pricing/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;129.00";}i:11;O:8:"stdClass":5:{s:5:"title";s:23:"PayFast Payment Gateway";s:5:"image";s:72:"https://www.woothemes.com/wp-content/uploads/2013/09/PayFast_Logo_75.png";s:7:"excerpt";s:70:"Take payments on your WooCommerce store via PayFast (redirect method).";s:4:"link";s:117:"http://www.woothemes.com/products/payfast-payment-gateway/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:9:"&#36;0.00";}i:12;O:8:"stdClass":5:{s:5:"title";s:10:"PayPal Pro";s:5:"image";s:86:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2011/09/paypalpro.png";s:7:"excerpt";s:69:"Take credit card payments directly on your checkout using PayPal Pro.";s:4:"link";s:104:"http://www.woothemes.com/products/paypal-pro/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:13;O:8:"stdClass":5:{s:5:"title";s:35:"Print Invoices &#038; Packing lists";s:5:"image";s:0:"";s:7:"excerpt";s:49:"Print your WooCommerce invoices and packing lists";s:4:"link";s:122:"http://www.woothemes.com/products/print-invoices-packing-lists/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:14;O:8:"stdClass":5:{s:5:"title";s:15:"Product Bundles";s:5:"image";s:0:"";s:7:"excerpt";s:149:"Create and offer highly configurable product bundles, kits and assemblies that consist of simple and variable items - both physical and downloadable.";s:4:"link";s:109:"http://www.woothemes.com/products/product-bundles/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:15;O:8:"stdClass":5:{s:5:"title";s:21:"Checkout Field Editor";s:5:"image";s:0:"";s:7:"excerpt";s:128:"The checkout field editor provides you with an interface to add, edit and remove fields shown on your WooCommerce checkout page.";s:4:"link";s:127:"http://www.woothemes.com/products/woocommerce-checkout-field-editor/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:16;O:8:"stdClass":5:{s:5:"title";s:24:"Product CSV Import Suite";s:5:"image";s:0:"";s:7:"excerpt";s:91:"Import, merge, and export products and variations to and from WooCommerce using a CSV file.";s:4:"link";s:118:"http://www.woothemes.com/products/product-csv-import-suite/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:17;O:8:"stdClass":5:{s:5:"title";s:26:"Catalog Visibility Options";s:5:"image";s:0:"";s:7:"excerpt";s:80:"Transform WooCommerce into an online catalog by removing eCommerce functionality";s:4:"link";s:120:"http://www.woothemes.com/products/catalog-visibility-options/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:18;O:8:"stdClass":5:{s:5:"title";s:13:"Smart Coupons";s:5:"image";s:0:"";s:7:"excerpt";s:119:"Everyone&#039;s favorite “all in one” solution for store credits, gift certificates, discount coupons and vouchers.";s:4:"link";s:107:"http://www.woothemes.com/products/smart-coupons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;99.00";}i:19;O:8:"stdClass":5:{s:5:"title";s:29:"Gravity Forms Product Add-ons";s:5:"image";s:0:"";s:7:"excerpt";s:39:"Powerful product add-ons, Gravity style";s:4:"link";s:115:"http://www.woothemes.com/products/gravity-forms-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;99.00";}i:20;O:8:"stdClass":5:{s:5:"title";s:17:"Authorize.Net CIM";s:5:"image";s:75:"https://www.woothemes.com/wp-content/uploads/2013/04/authorize-net-logo.png";s:7:"excerpt";s:68:"Authorize CIM gateway with support for pre-orders and subscriptions.";s:4:"link";s:111:"http://www.woothemes.com/products/authorize-net-cim/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:21;O:8:"stdClass":5:{s:5:"title";s:23:"ShipStation Integration";s:5:"image";s:69:"https://www.woothemes.com/wp-content/uploads/2012/08/shipstation1.png";s:7:"excerpt";s:161:"Fulfill all your Woo orders (and wherever else you sell) quickly and easily using ShipStation, the #1 choice for online retailers. Try it free for 30 days today!";s:4:"link";s:117:"http://www.woothemes.com/products/shipstation-integration/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:9:"&#36;0.00";}i:22;O:8:"stdClass":5:{s:5:"title";s:21:"FedEx Shipping Method";s:5:"image";s:98:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2013/01/FedEx_Logo_Wallpaper.jpeg";s:7:"excerpt";s:92:"Get shipping rates from the FedEx API which handles both domestic and international parcels.";s:4:"link";s:115:"http://www.woothemes.com/products/fedex-shipping-module/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;79.00";}i:23;O:8:"stdClass":5:{s:5:"title";s:17:"Shipment Tracking";s:5:"image";s:0:"";s:7:"excerpt";s:48:"Add shipment tracking information to your orders";s:4:"link";s:111:"http://www.woothemes.com/products/shipment-tracking/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:24;O:8:"stdClass":5:{s:5:"title";s:10:"TradeGecko";s:5:"image";s:71:"https://www.woothemes.com/wp-content/uploads/2015/09/tradegecko_new.jpg";s:7:"excerpt";s:216:"TradeGecko is a cloud-based inventory management platform for retailers and wholesalers that want effortless commerce. Enhance your business with advanced inventory and order management connected to your WooCommerce.";s:4:"link";s:116:"http://www.woothemes.com/products/woocommerce-tradegecko/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:9:"&#36;0.00";}}s:16:"payment-gateways";a:24:{i:0;O:8:"stdClass":5:{s:5:"title";s:15:"Pay with Amazon";s:5:"image";s:116:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2013/08/AP_HLogo_215x35-3012530377._V